<script>
export default {
  name: "Navigation",
  data() {
    return {
      routes: [
        { path: "biology", name: "Biology" },
        { path: "coding", name: "Coding" },
        { path: "about", name: "About / Contact" }
      ]
    };
  },
  methods: {
    closeNavigation() {
      document.querySelector(".navigation-overlay").classList.add("hidden");
    },
    toggleNavigation() {
      document.querySelector(".navigation-overlay").classList.toggle("hidden");
    },
    toggleColorMode() {
      if (document.documentElement.getAttribute("data-theme") === "light") {
        document.documentElement.setAttribute("data-theme", "dark");
      } else {
        document.documentElement.setAttribute("data-theme", "light");
      }
      this.toggleIcon();
    },
    toggleIcon() {
      document.querySelector("#sun-icon").classList.toggle("hidden");
      document.querySelector("#moon-icon").classList.toggle("hidden");

      const favicon = document.querySelector("#favicon");
      if (favicon.getAttribute("href") === "/fav_light.ico") {
        favicon.setAttribute("href", "/fav_dark.ico");
      } else {
        favicon.setAttribute("href", "/fav_light.ico");
      }
    }
  }
};
</script>

